def get_links():
    result = requests.get(URL)
    if result.status_code == 200:
        soup = BeautifulSoup(result.text, 'html.parser')
        side_menu = soup.find("div", {"id" : "mf_product_categories-9"})
        items = side_menu.findAll("ul", {"class" : "children"})
        for item in items:
            anchor = item.find("a")
            links.append(anchor['href'])
            print(links)

if __name__ == "__main__":
    get_links()